import React, { useState } from 'react';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    // In a real app, you'd perform authentication here, e.g., by making an API request to your backend.
    // For simplicity, we'll simulate a successful login.

    // Replace this with actual authentication logic.
    const isValidUser = true; // Simulated authentication success

    if (isValidUser) {
      alert('Login successful'); // Simulate a successful login
    } else {
      alert('Login failed. Invalid email or password.');
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Login</button>
    </div>
  );
};

export default Login;
